import React, { Component } from 'react'
import Newsitem from './Newsitem'

export class News extends Component {
  constructor(){
    super();
    this.state = {
        articles: [],
        loading: false,
        page: 1
    }
  }

  async componentDidMount(){
    let url = "https://newsapi.org/v2/top-headlines?country=in&apiKey=c488e58aa8db4c669697d64be8d27adb&page=1&pageSize=12";
    let data = await fetch(url);
    let parsedData = await data.json()
    console.log(parsedData);
    this.setState({articles: parsedData.articles, totalResults: parsedData.totalResults})
  }

  handlePreClick = async ()=>{
    let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=c488e58aa8db4c669697d64be8d27adb&page=${this.state.page - 1}&pageSize=12`;
    let data = await fetch(url);
    let parsedData = await data.json()
    console.log(this.state.page);
    console.log(parsedData);
    this.setState({
        page: this.state.page - 1,
        articles: parsedData.articles
    })
  }
  handleNextClick = async ()=>{ 
    if(Math.ceil(this.state.totalResults/20) < this.state.page+1){

    }
    else{
        let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=c488e58aa8db4c669697d64be8d27adb&page=${this.state.page + 1}&pageSize=12`;
        let data = await fetch(url);
        let parsedData = await data.json()
        console.log(this.state.page);
        console.log(parsedData);
        this.setState({
            page: this.state.page + 1,
            articles: parsedData.articles
        })
    }
  }

  render() {
    return (
      <div className="container my-3">
        <h2>IndiaNews - Top Headline</h2>
        <div className="row">
        {this.state.articles.map((element)=>{
            return <div className="col-md-4" key={element.url}>
            <Newsitem title = {element.title?element.title:""} description={element.description?element.description:""} imageUrl = {element.urlToImage} newsUrl ={element.url}/>
          </div> 
        })}
        </div>
        <div className='container d-flex justify-content-between'>
        {/* console.log(this.state.page); */}

        <button disabled={this.state.page<=1} type="button" className="btn btn-dark" onClick={this.handlePreClick}> &larr; Previous</button>
        <button type="button" className="btn btn-dark" onClick={this.handleNextClick}>Next &rarr;</button>
        </div>
      </div>
    )
  }
}

export default News
